import { configureStore } from "@reduxjs/toolkit";

const notificationReducer = (state = null, action) => {
  switch (action.type) {
    case "NOTIFY":
      return state;
    case "SILENT":
      return null;
    default:
      return null;
  }
};

export default notificationReducer;
