const notificationReducer = (state = null, action) => {
  switch (action.type) {
    case "NOTIFY":
      return state;
    case "SILENT":
      return null;
    default:
      return null;
  }
};

export const setNotification = (text) => {
  return {
    type: "NOTIFY",
    data: { text },
  };
};

export default notificationReducer;
