const anecdoteEntryReducer = (state = [], action) => {
  switch (action.type) {
    case "ADD":
      return state.push(action.blog);
    case "VOTE":
      return state.map((anecdote) =>
        anecdote.id === action.id
          ? { ...anecdote, votes: anecdote.votes + 1 }
          : anecdote
      );
    default:
      return state;
  }
};

export const addAnecdote = (anecdote) => {
  anecdote.id = Math.round(Math.random() * 10000);
  return {
    type: "ADD",
    anecdote,
  };
};

const initialAnecdotes = [
  {
    content: "If it hurts, do it more often",
    author: "Jez Humble",
    info: "https://martinfowler.com/bliki/FrequencyReducesDifficulty.html",
    votes: 0,
    id: 1,
  },
  {
    content: "Premature optimization is the root of all evil",
    author: "Donald Knuth",
    info: "http://wiki.c2.com/?PrematureOptimization",
    votes: 0,
    id: 2,
  },
];

export const initializeAnectodes = () => {
  initialAnecdotes.forEach((anecdote) => addAnecdote(anecdote));
};

export default anecdoteEntryReducer;
