const anecdoteEntryReducer = (state = [], action) => {
  switch (action.type) {
    case "ADD":
      return state.push(action.blog);
    default:
      return state;
  }
};

export const addAnecdote = (anecdote) => {
  return {
    type: "ADD",
    anecdote,
  };
};

export default anecdoteEntryReducer;
